import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';

const CatEdit = () => {
    const [catBreeds, setCatBreeds] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5555/api/cats')
            .then(response => response.json())
            .then(data => setCatBreeds(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    const columns = [
        {
            dataField: 'name',
            text: 'Name',
        },
        {
            dataField: 'origin',
            text: 'Origin',
        },
        {
            dataField: 'description',
            text: 'Description',
        },
    ];

    const options = {
        paginationSize: 7, // Show 7 items per page
        pageStartIndex: 1, // Start page index (optional)
        hideSizePerPage: true, // Hide "Items per page" dropdown (optional)
        hidePageListOnlyOnePage: true, // Hide pagination if there's only one page (optional)
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center">Cat Breeds</h1>
            <BootstrapTable
                keyField="id"
                data={catBreeds}
                columns={columns}
                pagination={paginationFactory(options)}
            />
        </div>
    );
};

export default CatEdit;
