import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddCatPage = () => {
  const navigate = useNavigate();

  const [name, setName] = useState('');
  const [origin, setOrigin] = useState('');
  const [temperament, setTemperament] = useState('');
  const [colors, setColors] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');

  const submitHandler = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('origin', origin);
      formData.append('temperament', temperament);
      formData.append('colors', colors.split(',').map(color => color.trim())); // Assuming colors are entered as comma-separated values
      formData.append('description', description);
      formData.append('image', image);

      // First, upload the image
      const uploadResponse = await axios.post('http://localhost:5555/api/uploads', formData, { withCredentials: true });
      setImage(uploadResponse.data.image);
      alert('Image uploaded successfully');

      // Then, submit the rest of the form data
      const response = await axios.post('/api/cats', {
        ...formData,
        image: uploadResponse.data.image
      }, { withCredentials: true });

      navigate('/cats/dashboard');
    } catch (error) {
      alert(error?.response?.data?.message || error.message);
    }
  }

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
  }

  return (
    <div>
      <h1>Add New Cat</h1>
      <form onSubmit={submitHandler}>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />

        <label>Origin:</label>
        <input type="text" value={origin} onChange={(e) => setOrigin(e.target.value)} required />

        <label>Temperament:</label>
        <input type="text" value={temperament} onChange={(e) => setTemperament(e.target.value)} required />

        <label>Colors:</label>
        <input type="text" value={colors} onChange={(e) => setColors(e.target.value)} required />

        <label>Description:</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />

        <label>Image:</label>
        <input type="file" onChange={handleImageChange} required />

        <button type="submit">Add Cat</button>
      </form>
    </div>
  );
}

export default AddCatPage;
