import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';
import { Link } from 'react-router-dom';


const BirdEdit = () => {
    const [birdBreeds, setBirdBreeds] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [searchTerm, setSearchTerm] = useState('');
    const itemsPerPage = 6;

    useEffect(() => {
        fetch('http://localhost:5555/api/birds')
            .then(response => response.json())
            .then(data => setBirdBreeds(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);

    // Calculate the current items to display
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = birdBreeds
        .filter(bird => bird.name.toLowerCase().includes(searchTerm.toLowerCase()))
        .slice(indexOfFirstItem, indexOfLastItem);
        const deleteBird = async (id) => {
            try{
                await axios.delete(`http://localhost:5555/api/bird/${id}`, { withCredentials : true });
                const response = await axios.get('http://localhost:5555/api/cats', { withCredentials : true })
                setCatBreeds(response.data)
            }
            catch(error){
                console.error(error)
            }
        }

    // Change page
    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    return (
        <div className="container mt-5" style={{marginBottom: '80px'}}>
            <div className="d-flex justify-content-between align-items-center mb-4">
                <button className="btn btn-success">Add Bird</button>
                <h1 className="text-center">Bird Breeds</h1>
                <input
                    type="text"
                    placeholder="Search..."
                    className="form-control"
                    style={{ width: '20%' }}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th className="text-center">Name</th>
                        <th className="text-center">Origin</th>
                        <th className="text-center">Description</th>
                        <th className="text-center">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {currentItems.map(bird => (
                        <tr key={bird.id}>
                            <td className="text-center">{bird.name}</td>
                            <td className="text-center">{bird.place_of_found}</td>
                            <td className="text-center">{bird.description}</td>
                            <td className="text-center">
                            <Link to={`${bird._id}/edit`}>
                                <button className="btn btn-primary btn-block">Edit</button></Link>
                                <button className="btn btn-danger btn-block">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <div className="pagination" style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                {[...Array(Math.ceil(birdBreeds.length / itemsPerPage)).keys()].map(number => (
                    <button
                        key={number}
                        onClick={() => paginate(number + 1)}
                        style={{ margin: '0 5px', padding: '10px 15px', fontSize: '1rem' }}
                    >
                        {number + 1}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default BirdEdit;
