import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPage = () => {
  const [category, setCategory] = useState('');
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!category) return;

    setLoading(true);
    axios.get(`http://localhost:3000/api/${category}`)
      .then(response => {
        // Normalize the data structure to ensure consistency
        const normalizedData = response.data.map(item => ({
          id: item.id,
          name: item.name,
          image: item.image,
          origin: item.place_of_origin || item.origin // Use "place_of_origin" for birds, "origin" for cats and dogs
        }));
        setData(normalizedData);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setLoading(false);
      });
  }, [category]);

  const handleCategoryChange = event => {
    setCategory(event.target.value);
  };

  const renderTable = () => {
    if (loading) {
      return <p>Loading...</p>;
    }

    if (!data.length) {
      return <p>No data available.</p>;
    }

    return (
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Photo</th>
            <th>Origin</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td><img src={item.image} alt={item.name} height="100px" /></td>
              <td>{item.origin}</td>
              <td>
                <button onClick={() => handleEdit(item)}>Edit</button>
                <button onClick={() => handleUpdate(item)}>Update</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  const handleEdit = item => {
    // Logic for editing the item
    console.log('Edit:', item);
  };

  const handleUpdate = item => {
    // Logic for updating the item
    console.log('Update:', item);
  };

  return (
    <div>
      <h1>Admin Page</h1>
      <div>
        <label htmlFor="category">Select Category:</label>
        <select id="category" value={category} onChange={handleCategoryChange}>
          <option value="">Select...</option>
          <option value="birds">Birds</option>
          <option value="cats">Cats</option>
          <option value="dogs">Dogs</option>
        </select>
      </div>
      {renderTable()}
    </div>
  );
};

export default AdminPage;
