import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';

const DogEdit = () => {
    const [dogBreeds, setdogBreeds] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [searchTerm, setSearchTerm] = useState('');
    const itemsPerPage = 6;

    useEffect(() => {
        fetch('http://localhost:5555/api/dogs')
            .then(response => response.json())
            .then(data => setdogBreeds(data))
            .catch(error => console.error('Error fetching data:', error));
    }, []);
    // Calculate the current items to display
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = dogBreeds
        .filter(dog => dog.name.toLowerCase().includes(searchTerm.toLowerCase()))
        .slice(indexOfFirstItem, indexOfLastItem);
        const paginate = (pageNumber) => setCurrentPage(pageNumber);

    return (
        <div className="container mt-5" style={{marginBottom: '80px'}}>
            <div className="d-flex justify-content-between align-items-center mb-4">
                <button className="btn btn-success">Add Dog</button>
                <h1 className="text-center">Dog Breeds</h1>
                <input
                    type="text"
                    placeholder="Search..."
                    className="form-control"
                    style={{ width: '20%' }}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th className="text-center">Name</th>
                        <th className="text-center">Origin</th>
                        <th className="text-center">Description</th>
                        <th className="text-center">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {currentItems.map(dog => (
                        <tr key={dog.id}>
                            <td className="text-center">{dog.name}</td>
                            <td className="text-center">{dog.origin}</td>
                            <td className="text-center">{dog.description}</td>
                            <td className="text-center">
                                <button className="btn btn-primary btn-block">Edit</button>
                                <button 
                                className="btn btn-danger btn-block"
                                onClick={() => handleDelete(dog._id)}
                            >
                                Delete
                            </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <div className="pagination" style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
                {[...Array(Math.ceil(dogBreeds.length / itemsPerPage)).keys()].map(number => (
                    <button
                        key={number}
                        onClick={() => paginate(number + 1)}
                        style={{ margin: '0 5px', padding: '10px 15px', fontSize: '1rem' }}
                    >
                        {number + 1}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default DogEdit;
