import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

const EditDogForm = () => {
    const [dog, setDog] = useState({
        breed_group: '',
        colors: [],
        description: '',
        image: '',
        lifespan: '',
        name: '',
        origin: '',
        size: '',
        temperament: ''
    });
    const { id } = useParams(); // Get the id from the URL
    const history = useHistory();

    useEffect(() => {
        // Fetch the dog details from the API
        axios.get(`http://localhost:5555/api/dogs/${id}`)
            .then(response => {
                setDog(response.data); // Set the dog data in the state
            })
            .catch(error => console.error('Error fetching data:', error));
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setDog({ ...dog, [name]: value });
    };

    const handleColorChange = (index, value) => {
        const updatedColors = [...dog.colors];
        updatedColors[index] = value;
        setDog({ ...dog, colors: updatedColors });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:5555/api/dogs/${id}`, dog);
            history.push('/dogs'); // Redirect to the dogs list page
        } catch (error) {
            console.error('Error updating dog:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Input fields for editing the dog details */}
            <input type="text" name="name" value={dog.name} onChange={handleChange} />
            <input type="text" name="origin" value={dog.origin} onChange={handleChange} />
            <input type="text" name="breed_group" value={dog.breed_group} onChange={handleChange} />
            <input type="text" name="size" value={dog.size} onChange={handleChange} />
            <input type="text" name="lifespan" value={dog.lifespan} onChange={handleChange} />
            <input type="text" name="temperament" value={dog.temperament} onChange={handleChange} />
            <textarea name="description" value={dog.description} onChange={handleChange} />
            <input type="text" name="image" value={dog.image} onChange={handleChange} />
            {/* Map through colors array to create input fields for each color */}
            {dog.colors.map((color, index) => (
                <input
                    key={index}
                    type="text"
                    value={color}
                    onChange={(e) => handleColorChange(index, e.target.value)}
                />
            ))}
            <button type="submit">Update Dog</button>
        </form>
    );
};

export default EditDogForm;
