import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import CatImage from './cat.jpg'; // Import your cat image
import DogImage from './dog.jpg'; // Import your dog image
import BirdImage from './bird.jpg'; // Import your bird image

const HomePage = () => {
  const imageStyle = {
    width: '100%',
    height: '300px',
    objectFit: 'cover',
    borderRadius: '10px',
  };

  const cardStyle = {
    border: '2px solid #ccc',
    borderRadius: '10px',
    marginBottom: '20px',
    padding: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease',
  };

  const titleStyle = {
    textAlign: 'center',
    fontSize: '3rem',
    fontWeight: 'bold',
    color: '#008000', // Green color
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.2)', // Nice shadow
    marginBottom: '20px',
  };

  const descriptionStyle = {
    fontSize: '1.2rem',
    color: '#666', // Dark gray color
    marginTop: '10px',
    textAlign: 'center',
  };


  return (
    <Container style={{ paddingTop: '30px', paddingBottom: '30px' }}>
      <h1 style={titleStyle}>PET EXPO</h1>
      <p style={descriptionTitleStyle}>
        Welcome to the Pet Expo! Explore our galleries to discover adorable cats, dogs, and birds.
      </p>
      <Row>
        <Col md={4}>
          <Link to="/cats" style={{ textDecoration: 'none', color: '#333' }}>
            <div style={cardStyle}>
              <img src={CatImage} alt="Cats" style={imageStyle} />
              <div style={descriptionStyle}>Discover our furry feline friends!</div>
            </div>
          </Link>
        </Col>
        <Col md={4}>
          <Link to="/dogs" style={{ textDecoration: 'none', color: '#333' }}>
            <div style={cardStyle}>
              <img src={DogImage} alt="Dogs" style={imageStyle} />
              <div style={descriptionStyle}>Meet our loyal canine companions!</div>
            </div>
          </Link>
        </Col>
        <Col md={4}>
          <Link to="/birds" style={{ textDecoration: 'none', color: '#333' }}>
            <div style={cardStyle}>
              <img src={BirdImage} alt="Birds" style={imageStyle} />
              <div style={descriptionStyle}>Explore the world of our feathered friends!</div>
            </div>
          </Link>
        </Col>
      </Row>

    </Container>
  );
};

export default HomePage;
