import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';

const CatEdit = () => {
    const [catBreeds, setCatBreeds] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [breedsPerPage] = useState(10); // Set the number of breeds per page

    useEffect(() => {
        fetch(`http://localhost:5555/api/cats?page=${currentPage}&limit=${breedsPerPage}`)
            .then(response => response.json())
            .then(data => setCatBreeds(data))
            .catch(error => console.error('Error fetching data:', error));
    }, [currentPage, breedsPerPage]);

    // Function to change page
    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    return (
        <div className="container mt-5">
            <h1 className="text-center">Cat Breeds</h1>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th className="text-center">Name</th>
                        <th className="text-center">Origin</th>
                        <th className="text-center">Description</th>
                        <th className="text-center">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {catBreeds.map(cat => (
                        <tr key={cat.id}>
                            <td className="text-center">{cat.name}</td>
                            <td className="text-center">{cat.origin}</td>
                            <td className="text-center">{cat.description}</td>
                            <td className="text-center">
                                <button className="btn btn-primary btn-block">Edit</button>
                                <button className="btn btn-danger btn-block">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <nav>
                <ul className='pagination'>
                    {/* Here you would dynamically create your pagination links */}
                    {/* This is just a placeholder for demonstration */}
                    <li className='page-item'>
                        <a onClick={() => paginate(1)} className='page-link'>
                            1
                        </a>
                    </li>
                    {/* Add more pagination links as needed */}
                </ul>
            </nav>
        </div>
    );
};

export default CatEdit;
