import React, { useEffect, useState } from 'react';
import Table from 'react-bootstrap/Table';

// Simple Pagination Component
const Pagination = ({ breedsPerPage, totalBreeds, paginate }) => {
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(totalBreeds / breedsPerPage); i++) {
        pageNumbers.push(i);
    }

    return (
        <nav>
            <ul className='pagination'>
                {pageNumbers.map(number => (
                    <li key={number} className='page-item'>
                        <a onClick={() => paginate(number)} className='page-link'>
                            {number}
                        </a>
                    </li>
                ))}
            </ul>
        </nav>
    );
};

const CatEdit = () => {
    const [catBreeds, setCatBreeds] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [breedsPerPage] = useState(10); // Set the number of breeds per page
    const [totalBreeds, setTotalBreeds] = useState(0);

    useEffect(() => {
        fetch(`http://localhost:5555/api/cats?page=${currentPage}&limit=${breedsPerPage}`)
            .then(response => response.json())
            .then(data => {
                setCatBreeds(data.breeds);
                setTotalBreeds(data.total);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, [currentPage, breedsPerPage]);

    // Function to change page
    const paginate = (pageNumber) => setCurrentPage(pageNumber);

    return (
        <div className="container mt-5">
            <h1 className="text-center">Cat Breeds</h1>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th className="text-center">Name</th>
                        <th className="text-center">Origin</th>
                        <th className="text-center">Description</th>
                        <th className="text-center">Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {catBreeds.map(cat => (
                        <tr key={cat.id}>
                            <td className="text-center">{cat.name}</td>
                            <td className="text-center">{cat.origin}</td>
                            <td className="text-center">{cat.description}</td>
                            <td className="text-center">
                                <button className="btn btn-primary btn-block">Edit</button>
                                <button className="btn btn-danger btn-block">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
            <Pagination breedsPerPage={breedsPerPage} totalBreeds={totalBreeds} paginate={paginate} />
        </div>
    );
};

export default CatEdit;
