import React from 'react';
import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import CatImage from '../assets/images/cat.jpg'
import DogImage from '../assets/images/dog.jpg'; 
import BirdImage from '../assets/images/bird.jpg'; 

const HomePage = () => {
  const imageStyle = {
    width: '100%',
    height: '340px',
    objectFit: 'cover',
    borderRadius: '10px',
  };

  const cardStyle = {
    border: '2px solid #ccc',
    borderRadius: '10px',
    marginBottom: '20px',
    padding: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease',
  };

  const titleStyle = {
    textAlign: 'center',
    fontSize: '3rem',
    fontWeight: 'bold',
    color: '#008000', 
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.2)',
    margin: '20px 0 20px 0',
  };

  const descriptionStyle = {
    fontSize: '1.2rem',
    color: '#666', 
    marginTop: '10px',
    textAlign: 'center',
  };

  const descriptionTitleStyle = {
    fontSize: '1.2rem',
    color: '#666', 
    margin: '30px 0 40px 0',
    textAlign: 'center',
  };

  return (
    <>
      <Container>
        <h1 style={titleStyle}>PET EXPO</h1>
        <p style={descriptionTitleStyle}>
          Welcome to the Pet Expo! Explore our galleries to discover adorable cats, dogs, and birds.
        </p>
        <Row>
          <Col md={4}>
            <Link to="/cats/dashboard" style={{ textDecoration: 'none', color: '#333' }}>
              <div style={cardStyle}>
                <img src={CatImage} alt="Cats" style={imageStyle} />
                <div style={descriptionStyle}>View All Cats!</div>
              </div>
            </Link>
          </Col>
          <Col md={4}>
            <Link to="/dogs/dashboard/" style={{ textDecoration: 'none', color: '#333' }}>
              <div style={cardStyle}>
                <img src={DogImage} alt="Dogs" style={imageStyle} />
                <div style={descriptionStyle}>Meet our loyal canine companions!</div>
              </div>
            </Link>
          </Col>
          <Col md={4}>
            <Link to="/birds/dashboard" style={{ textDecoration: 'none', color: '#333' }}>
              <div style={cardStyle}>
                <img src={BirdImage} alt="Birds" style={imageStyle} />
                <div style={descriptionStyle}>Explore the world of our feathered friends!</div>
              </div>
            </Link>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default HomePage;
