import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Container } from 'react-bootstrap';
import logo from '../assets/images/logo.png';
import axios from 'axios';

const Header = () => {
  const isAuth = localStorage.getItem('isAuth');
  const navigate = useNavigate();
  const handleLogout = async() => {
    try {
      await axios.post('http://localhost:5555/api/user/logout', { withCredentials: true });
      localStorage.removeItem('isAuth');
      navigate('/')
    } catch (error) {
      console.log(error);
    }
  }  
  return (
    <header style={{ display:'flex', justifyContent: 'space-between', alignItems:'center', backgroundColor: '#7A9C59', color: '#fff', padding: '20px 0' }}>
        <a href={'/'} style={{marginLeft:'50px'}}>
            <img src={logo} height='35px' />
        </a>
        <div style={{marginRight:'50px'}}>
      <Container>
        <nav>
          <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
            <li style={{ display: 'inline', marginRight: '20px' }}>
              <Link to="/" style={{ textDecoration: 'none', color: '#fff' }}>
                HOMEPAGE
              </Link>
            </li>
            <li style={{ display: 'inline', marginRight: '20px' }}>
              <Link to="/about-us" style={{ textDecoration: 'none', color: '#fff' }}>
                ABOUT US
              </Link>
            </li>
            <li style={{ display: 'inline' }}>
              <Link to="/contact-us" style={{ textDecoration: 'none', color: '#fff' }}>
                CONTACT US
              </Link>
            </li>
            {isAuth &&<li><button onClick={handleLogout}>Logout</button>
            </li>}
          </ul>
        </nav>
      </Container>
      </div>
    </header>
  );
};

export default Header;
