import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditDogForm = () => {
    const [dog, setDog] = useState({
        breed_group: '',
        colors: [],
        description: '',
        image: '',
        lifespan: '',
        name: '',
        origin: '',
        size: '',
        temperament: ''
    });
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        axios.get(`http://localhost:5555/api/dogs/${id}`)
            .then(response => {
                setDog(response.data);
            })
            .catch(error => console.error('Error fetching data:', error));
    }, [id]);

    const handleChange = e => {
        const { name, value } = e.target;
        setDog({ ...dog, [name]: value });
    };

    const handleColorChange = (index, value) => {
        const updatedColors = [...dog.colors];
        updatedColors[index] = value;
        setDog({ ...dog, colors: updatedColors });
    };

    const handleSubmit = async e => {
        e.preventDefault();
        try {
            await axios.put(`http://localhost:5555/api/dogs/${id}`, dog);
            navigate('/dogs');
        } catch (error) {
            console.error('Error updating dog:', error);
        }
    };

    const inputStyle = {
        padding: '10px',
        margin: '10px 0',
        borderRadius: '5px',
        border: '1px solid #ddd'
    };

    const formStyle = {
        display: 'flex',
        flexDirection: 'column',
        maxWidth: '300px',
        margin: 'auto'
    };

    return (
        <form onSubmit={handleSubmit} style={formStyle}>
            <input style={inputStyle} type="text" name="name" value={dog.name} onChange={handleChange} placeholder="Name" />
            <input style={inputStyle} type="text" name="origin" value={dog.origin} onChange={handleChange} placeholder="Origin" />
            <input style={inputStyle} type="text" name="breed_group" value={dog.breed_group} onChange={handleChange} placeholder="Breed Group" />
            <input style={inputStyle} type="text" name="size" value={dog.size} onChange={handleChange} placeholder="Size" />
            <input style={inputStyle} type="text" name="lifespan" value={dog.lifespan} onChange={handleChange} placeholder="Lifespan" />
            <input style={inputStyle} type="text" name="temperament" value={dog.temperament} onChange={handleChange} placeholder="Temperament" />
            <textarea style={inputStyle} name="description" value={dog.description} onChange={handleChange} placeholder="Description" />
            <input style={inputStyle} type="text" name="image" value={dog.image} onChange={handleChange} placeholder="Image URL" />
            {dog.colors.map((color, index) => (
                <input
                    key={index}
                    style={inputStyle}
                    type="text"
                    value={color}
                    onChange={e => handleColorChange(index, e.target.value)}
                    placeholder={`Color #${index + 1}`}
                />
            ))}
            <button style={{ ...inputStyle, backgroundColor: '#007bff', color: 'white' }} type="submit">Update Dog</button>
        </form>
    );
};

export default EditDogForm;
